import React, { useState, useEffect } from 'react';
import './App.css';
import profilePhoto from './assets/images/profile-photo.jpg';

function App() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setDarkMode(true);
      document.body.classList.add('dark-mode');
    }
  }, []);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
    if (!darkMode) {
      document.body.classList.add('dark-mode');
      localStorage.setItem('theme', 'dark');
    } else {
      document.body.classList.remove('dark-mode');
      localStorage.setItem('theme', 'light');
    }
  };

  return (
    <div className="App">
      <nav className="Navbar">
        <ul>
          <li><a href="#home">Home</a></li>
          <li><a href="#skills">Skills</a></li>
          <li><a href="#projects">Projects</a></li>
          <li><a href="#contact">Contact</a></li>
          <li>
            <label className="Theme-toggle">
              <input type="checkbox" checked={darkMode} onChange={toggleTheme} />
              <span className="slider"></span>
            </label>
          </li>
        </ul>
      </nav>
      <section id="home" className="Home">
        <img src={profilePhoto} alt="Profile" className="Profile-photo" />
        <h1 className="App-title">Hi, I'm Zeeshan Javaid</h1>
        <p className="App-intro">
          A passionate software developer with expertise in web and mobile applications. Explore my work and get in touch!
        </p>
      </section>
      <section id="skills" className="Skills">
        <h2>Skills</h2>
        <div className="Skills-container">
          <div className="Skills-tile">Skill 1</div>
          <div className="Skills-tile">Skill 2</div>
          <div className="Skills-tile">Skill 3</div>
          <div className="Skills-tile">Skill 4</div>
        </div>
      </section>
      <section id="projects" className="Projects">
        <h2>Projects</h2>
        <p>Details about projects...</p>
      </section>
      <section id="contact" className="Contact">
        <h2>Contact</h2>
        <p>Contact details...</p>
      </section>
    </div>
  );
}

export default App;
